	public void remove_vehicle() {
		// begin-user-code
		// TODO Auto-generated method stub

		if (vehicle.isEmpty()) {
			System.out.print("\nThe list is empty. There is no vehicle in the list.\n\n");
		} else {
			System.out.print("\nEnter the vehicle ID that you want to remove: ");
			int to_remove_id = Application.scan.nextInt();
			Application.scan.nextLine();

			Iterator<Vehicle> itr = vehicle.iterator();
			Vehicle each_vehicle;

			while (itr.hasNext()) {
				each_vehicle = itr.next();
				int orig_id = (Integer) each_vehicle.getVehicle_id();
				if (orig_id == to_remove_id) {
					vehicle.remove(each_vehicle);
					System.out.print("\nThe vehicle has been removed successfully\n\n");
				}
			}
		}
		// end-user-code
	}

