	public void load_cargo_in_vehicle_cargo_space() {
		// begin-user-code
		// TODO Auto-generated method stub
		System.out.println("Specify the type of vehicle you want to load cargo");
		System.out.println("1. Truck\n2. Trailer\n3. Semi-trailer\n");

		System.out.print("Enter the number: ");
		int user_selection_load = Application.scan.nextInt();
		Application.scan.nextLine();
		//System.out.println(user_selection);
		if (user_selection_load == 1) {
			if (vehicle.isEmpty()) {
				System.out.print("\nThe list is empty. There is no vehicle in the list.\n\n");
			} else {
				System.out.print("\nEnter the vehicle ID that you want to load cargo: ");
				int to_load_id = Application.scan.nextInt();
				Application.scan.nextLine();

				Iterator<Vehicle> itr = vehicle.iterator();
				Vehicle each_vehicle;

				while (itr.hasNext()) {
					each_vehicle = itr.next();
					int orig_veh_id = (Integer) each_vehicle.getVehicle_id();
					if (orig_veh_id == to_load_id) {
						each_vehicle.load_vehicle_cargo();
					}
				}
			}

		}
		// end-user-code
	}

